package week03;
import java.math.*;

public class 예제04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double P20 = (20*20*Math.PI);
		double P30 = (30*30*Math.PI);
		System.out.printf("20cm 피자 2개의 면적 = %.4f\n" , 2*P20);
		System.out.printf("30cm 피자 면적 = %.4f\n" , P30);
		
		System.out.println("30cm 피자 한 개를 주문하세요.");

	}

}
